(74, 1, '2020-02-08 10:24:00', '2020-02-08 02:24:00', '<!-- wp:paragraph -->\n<p><strong>\"</strong>&nbsp;&nbsp;10月16日，盛大开业！一样的串串，不一样的味道！<strong>\"&nbsp;</strong><br></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>&nbsp; &nbsp; &nbsp; &nbsp; 马玉林串串香.麻辣烫（珠市口总店）忙忙碌碌三个月的时间，终于要和广大顾客朋友们见面了😜ོ店内奢华个性的装修风格加上咕噜噜美味的串串香与麻麻辣辣香味百里的麻辣烫😍ི让您会体验到无比的美味和用餐环境ღ💌ღ</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>马玉林串串香.珠市口总店（清真）</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>【有咕噜噜美味的串串香*😍*】</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>【有麻麻辣辣 香味百里的干拌麻辣烫୨😍୧】</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>【有好吃过瘾的烧烤大串和烤海鲜ʚ😍ɞ】</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>满满的心意，满满的情！</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p><strong>吃管够</strong>：锅底5折&nbsp;<br></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p><strong>嗨翻天</strong>：送干拌麻辣粉一份/桌&nbsp;<br></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p><strong>送不停</strong>：来吃就送代金券&nbsp;<br></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:image {\"id\":131} -->\n<figure class=\"wp-block-image\"><img src=\"http://slkqz.com/wp-content/uploads/2019/10/2-4.jpg\" alt=\"\" class=\"wp-image-131\"/></figure>\n<!-- /wp:image -->\n\n<!-- wp:separator -->\n<hr class=\"wp-block-separator\"/>\n<!-- /wp:separator -->\n\n<!-- wp:paragraph -->\n<p>电话：010-67018141&nbsp;<br></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>地址：北京市东城区金鱼池中街一号院一号楼二单元107</p>\n<!-- /wp:paragraph -->', '马玉林串串香.开业酬宾 打折打倒骨折', '', 'publish', 'open', 'open', '', '%e9%a9%ac%e7%8e%89%e6%9e%97%e4%b8%b2%e4%b8%b2%e9%a6%99-%e5%bc%80%e4%b8%9a%e9%85%ac%e5%ae%be-%e6%89%93%e6%8a%98%e6%89%93%e5%80%92%e9%aa%a8%e6%8a%98', '', '', '2020-02-09 01:00:11', '2020-02-08 17:00:11', '', 0, 'http://slkqz.com/?p=74', 0, 'post', '', 0),
